import React, { useEffect, useState } from "react";
import { getMovieDetail, watchMovie, getTvSeasons } from "../api.js";
import { useParams, useNavigate } from "react-router-dom";
import imageNull from "./image.png";
import backImage from "../assets/back.png";
import "./MovieDetail.css";
import MovieList from "../components/MovieList";
import muteIcon from "../assets/mute.png";
import unmuteIcon from "../assets/unmute.png";

function MovieDetail() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [movieData, setMovieData] = useState({});
  const [videoUrl, setVideoUrl] = useState("");
  const [videoId, setVideoId] = useState("");
  const [isMuted, setIsMuted] = useState(true);

  const [showPlayer, setShowPlayer] = useState(false);
  const [showEpisodePicker, setShowEpisodePicker] = useState(false);

  const [tvSeasons, setTvSeasons] = useState([]); // season list
  const [selectedSeason, setSelectedSeason] = useState(null);

  const [episodes, setEpisodes] = useState([]); // episode per season

  useEffect(() => {
    getMovieDetail(id).then((data) => {
      setMovieData(data);

      // If SERIES: fetch season data
      if (data.first_air_date) {
        getTvSeasons(id).then((tv) => {
          setTvSeasons(tv.seasons); // tmdb gives array of seasons
        });
      }
    });

    // Trailer
    watchMovie(id).then((videoData) => {
      const vid = videoData?.[0];
      if (vid) {
        setVideoId(vid.key);
        setVideoUrl(`https://www.youtube.com/embed/${vid.key}`);
      }
    });

    window.scrollTo(0, 0);
  }, [id]);


  const isSeries = !!movieData.first_air_date;
  const title = movieData.original_title || movieData.name;
  const releaseDate = movieData.release_date || movieData.first_air_date;
  const voteAverage = movieData.vote_average
    ? parseFloat(movieData.vote_average).toFixed(1)
    : "N/A";
  const genreNames = movieData.genres
    ? movieData.genres.map((g) => g.name).join(", ")
    : "";

  /* ───────────────────────────────
      PLAY MOVIE DIRECTLY (MOVIE)
  ─────────────────────────────── */
  const playMovie = () => {
    setShowPlayer(true);
  };

  /* ───────────────────────────────
      IF SERIES → OPEN SEASON PICKER
  ─────────────────────────────── */
  const openSeriesPicker = () => {
    setShowEpisodePicker(true);
  };

  /* ───────────────────────────────
      SELECT SEASON → LOAD EPISODES
  ─────────────────────────────── */
  const fetchEpisodes = async (seasonNumber) => {
    const resp = await fetch(
      `https://api.themoviedb.org/3/tv/${id}/season/${seasonNumber}?api_key=YOUR_API_KEY`
    );
    const data = await resp.json();
    setEpisodes(data.episodes);
    setSelectedSeason(seasonNumber);
  };

  /* ───────────────────────────────
      PLAY SPECIFIC EPISODE
  ─────────────────────────────── */
  const playEpisode = (episodeNumber) => {
    const videasyURL = `https://player.videasy.net/tv/${id}/${selectedSeason}/${episodeNumber}`;

    setVideoUrl(videasyURL);
    setShowEpisodePicker(false);
    setShowPlayer(true);
  };

  return (
    <div className="detailcont">
      <div className="firstcont">
        <button onClick={() => navigate(-1)} id="back">
          <img src={backImage} id="backimg" alt="Back" />
        </button>

        <img
          src={`https://image.tmdb.org/t/p/w300/${movieData.backdrop_path}`}
          className="backdropimg"
          onError={(e) => (e.target.src = imageNull)}
        />

        <div className="topdetail">
          <img
            src={`https://image.tmdb.org/t/p/w500/${movieData.poster_path}`}
            className="imgdt"
            onError={(e) => (e.target.src = imageNull)}
          />

          <div className="detailtext">
            <h2 id="dtitle">{title}</h2>

            <div className="dinfo">
              <div id="dratings"><p>{voteAverage}</p></div>
              <div id="rdate">
                <p>{releaseDate} • {movieData.runtime}m • {genreNames}</p>
              </div>
            </div>

            <p className="ovtext">{movieData.overview}</p>

            {/* PLAY BUTTON */}
            <button
              className="playbtn"
              onClick={() => {
                isSeries ? openSeriesPicker() : playMovie();
              }}
            >
              ▶ Play
            </button>
          </div>
        </div>
      </div>

      {/* TRAILER */}
      {videoUrl && (
        <div className="dvideo">
          <h2>Trailer</h2>
          <iframe
            src={`${videoUrl}?controls=0&autoplay=1&mute=${isMuted ? 1 : 0}`}
            className="dtrailer"
            allowFullScreen
          />
          <button onClick={() => setIsMuted(!isMuted)} className="mbtn">
            {isMuted ? <img src={muteIcon} /> : <img src={unmuteIcon} />}
          </button>
        </div>
      )}

      <MovieList />


      {/* ───────────────────────────────
          POPUP A: SEASON + EPISODE PICKER
      ─────────────────────────────── */}
      {showEpisodePicker && (
        <div className="player-overlay">
          <div className="picker-box">
            <button className="close-player" onClick={() => setShowEpisodePicker(false)}>✕</button>

            <h2>Select Season</h2>

            {tvSeasons.map((s) => (
              <button
                className="season-btn"
                key={s.season_number}
                onClick={() => fetchEpisodes(s.season_number)}
              >
                Season {s.season_number}
              </button>
            ))}

            {selectedSeason && (
              <>
                <h2>Episodes – Season {selectedSeason}</h2>

                <div className="episode-list">
                  {episodes.map((ep) => (
                    <div key={ep.episode_number} className="episode-item">
                      <span>
                        E{ep.episode_number} — {ep.name}
                      </span>
                      <button
                        className="ep-play-btn"
                        onClick={() => playEpisode(ep.episode_number)}
                      >
                        ▶
                      </button>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        </div>
      )}

      {/* ───────────────────────────────
          POPUP B: VIDEOASY PLAYER
      ─────────────────────────────── */}
      {showPlayer && (
        <div className="player-overlay">
          <div className="player-container">
            <button className="close-player" onClick={() => setShowPlayer(false)}>✕</button>

            <iframe
              src={videoUrl}
              allowFullScreen
              className="player-iframe"
            />
          </div>
        </div>
      )}

    </div>
  );
}

export default MovieDetail;
