import React, { useEffect, useState } from "react";
import { getMovieDetail, watchMovie } from "../api.js";
import { useParams, useNavigate } from "react-router-dom";
import imageNull from "./image.png";
import backImage from "../assets/back.png";
import "./MovieDetail.css";
import MovieList from "../components/MovieList";
import "../components/MovieList.css";
import muteIcon from "../assets/mute.png";
import unmuteIcon from "../assets/unmute.png";
import axios from "axios";

function MovieDetail() {
  const { id } = useParams();
  const [movieData, setMovieData] = useState({});
  const [videoUrl, setVideoUrl] = useState("");
  const [videoId, setVideoId] = useState("");
  const [isMuted, setIsMuted] = useState(true);
  const [showPlayer, setShowPlayer] = useState(false);

  // ðŸ”¥ NEW STATES
  const [showEpisodePopup, setShowEpisodePopup] = useState(false);
  const [seasons, setSeasons] = useState([]);
  const [episodes, setEpisodes] = useState([]);
  const [selectedSeason, setSelectedSeason] = useState(null);

  const navigate = useNavigate();

  const TMDB_KEY = "YOUR_TMDB_API_KEY"; // gunakan api sendiri

  // ============================================
  // FETCH MOVIE DETAIL + TRAILER
  // ============================================
  useEffect(() => {
    getMovieDetail(id).then((data) => {
      setMovieData(data);

      // jika series, ambil daftar season
      if (data.first_air_date) {
        setSeasons(data.seasons || []);
      }
    });

    window.scrollTo(0, 0);

    watchMovie(id).then((videoData) => {
      const videoResults = videoData;
      if (videoResults && videoResults.length > 0) {
        const videoKey = videoResults[0].key;
        const url = `https://www.youtube.com/embed/${videoKey}`;
        setVideoUrl(url);
        setVideoId(videoKey);
      }
    });
  }, [id]);

  const {
    original_title,
    name,
    release_date,
    first_air_date,
    poster_path,
    backdrop_path,
    overview,
    vote_average,
    runtime,
    genres,
  } = movieData;

  const title = original_title || name;
  const releaseDate = release_date || first_air_date;
  const voteAverage = vote_average ? parseFloat(vote_average).toFixed(1) : "N/A";
  const genreNames = genres ? genres.map((g) => g.name).join(", ") : "";

  const isSeries = !!first_air_date;

  // ============================================
  // FETCH EPISODES WHEN SEASON SELECTED
  // ============================================
  const fetchEpisodes = async (seasonNumber) => {
    const url = `https://api.themoviedb.org/3/tv/${id}/season/${seasonNumber}?api_key=${TMDB_KEY}`;
    const resp = await axios.get(url);
    setEpisodes(resp.data.episodes);
  };

  // ============================================
  // PLAY MOVIE OR OPEN SEASON SELECTOR
  // ============================================
  const handlePlay = () => {
    if (isSeries) {
      setShowEpisodePopup(true);
    } else {
      setShowPlayer(true);
    }
  };

  // ============================================
  // CONSTRUCT VIDEASY URL
  // ============================================
  const playEpisode = (season, episode) => {
    const videasyURL = `https://player.videasy.net/tv/${id}/${season}/${episode}`;
    setShowEpisodePopup(false);

    setTimeout(() => {
      setShowPlayer(true);
      setIframeURL(videasyURL);
    }, 200);
  };

  const [iframeURL, setIframeURL] = useState("");

  // ============================================
  // MOVIE PLAY URL
  // ============================================
  const movieURL = `https://player.videasy.net/movie/${id}`;

  const handleUnmute = () => setIsMuted((prev) => !prev);

  return (
    <div className="detailcont">
      <div className="dgrad"></div>

      <div className="firstcont">
        <button onClick={() => navigate(-1)} id="back">
          <img src={backImage} id="backimg" alt="Back" />
        </button>

        <img
          src={`https://image.tmdb.org/t/p/w300/${backdrop_path}`}
          alt="Backdrop"
          onError={(e) => (e.target.src = imageNull)}
          className="backdropimg"
        />

        <div className="topdetail">
          <img
            src={`https://image.tmdb.org/t/p/w500/${poster_path}`}
            alt="Loading..."
            onError={(e) => (e.target.src = imageNull)}
            className="imgdt"
          />

          <div className="detailtext">
            <h2 id="dtitle">{title}</h2>

            <div className="dinfo">
              <div id="dratings">
                <p>{voteAverage}</p>
              </div>
              <div id="rdate">
                <p>
                  {releaseDate} â€¢ {runtime}m â€¢ {genreNames}
                </p>
              </div>
            </div>

            <p className="ovtext">{overview}</p>

            {/* PLAY */}
            <button className="playbtn" onClick={handlePlay}>
              â–¶ Play
            </button>
          </div>
        </div>
      </div>

      {/* Trailer */}
      <div className="dvideo">
        <div className="dtitletrailer">
          <h2>Trailer</h2>
        </div>

        {videoUrl && (
          <iframe
            src={`${videoUrl}?controls=0&loop=1&autoplay=1&mute=${
              isMuted ? 1 : 0
            }&rel=0&playlist=${videoId}`}
            loop
            autoPlay
            width="1000"
            height="500"
            frameBorder="0"
            allowFullScreen
            className="dtrailer"
          />
        )}

        <button onClick={handleUnmute} className="mbtn">
          {isMuted ? <img src={muteIcon} /> : <img src={unmuteIcon} />}
        </button>
      </div>

      <div className="recom">
        <MovieList />
      </div>

      {/* ============================================================
          POPUP SEASON - EPISODE SELECTOR
         ============================================================ */}
      {showEpisodePopup && (
        <div className="episode-overlay">
          <div className="episode-box">
            <button
              className="close-player"
              onClick={() => setShowEpisodePopup(false)}
            >
              âœ•
            </button>

            <h2>Select Season</h2>

            <div className="season-list">
              {seasons.map((s) => (
                <button
                  key={s.season_number}
                  className="season-btn"
                  onClick={() => {
                    setSelectedSeason(s.season_number);
                    fetchEpisodes(s.season_number);
                  }}
                >
                  Season {s.season_number}
                </button>
              ))}
            </div>

            {selectedSeason && (
              <>
                <h3>Episodes</h3>
                <div className="episode-list">
                  {episodes.map((ep) => (
                    <div key={ep.episode_number} className="episode-item">
                      <span>
                        EP {ep.episode_number} â€” {ep.name}
                      </span>
                      <button
                        className="ep-play-btn"
                        onClick={() =>
                          playEpisode(selectedSeason, ep.episode_number)
                        }
                      >
                        â–¶
                      </button>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        </div>
      )}

      {/* ============================================================
          FINAL VIDEASY PLAYER POPUP
         ============================================================ */}
      {showPlayer && (
        <div className="player-overlay">
          <div className="player-container">
            <button
              className="close-player"
              onClick={() => setShowPlayer(false)}
            >
              âœ•
            </button>

            <iframe
              src={iframeURL || movieURL}
              allowFullScreen
              frameBorder="0"
              className="player-iframe"
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default MovieDetail;
