import React, { useEffect, useState } from "react";
import { getMovieDetail, watchMovie } from "../api.js";
import { useParams, useNavigate } from "react-router-dom";
import imageNull from "./image.png";
import backImage from "../assets/back.png";
import "./MovieDetail.css";
import MovieList from "../components/MovieList";
import "../components/MovieList.css";
import muteIcon from "../assets/mute.png";
import unmuteIcon from "../assets/unmute.png";

function MovieDetail() {
  const { id } = useParams();
  const [movieData, setMovieData] = useState({});
  const [videoUrl, setVideoUrl] = useState("");
  const [videoId, setVideoId] = useState("");
  const [isMuted, setIsMuted] = useState(true);
  const [showPlayer, setShowPlayer] = useState(false);    // POPUP CONTROL
  const navigate = useNavigate();

  useEffect(() => {
    getMovieDetail(id).then((data) => setMovieData(data));
    window.scrollTo(0, 0);

    watchMovie(id).then((videoData) => {
      const videoResults = videoData;
      if (videoResults && videoResults.length > 0) {
        const videoKey = videoResults[0].key;
        const url = `https://www.youtube.com/embed/${videoKey}`;
        setVideoUrl(url);
        setVideoId(videoKey);
      }
    });
  }, [id]);

  const {
    original_title,
    name,
    release_date,
    first_air_date,
    poster_path,
    backdrop_path,
    overview,
    vote_average,
    runtime,
    genres,
  } = movieData;

  const title = original_title || name;
  const releaseDate = release_date || first_air_date;
  const voteAverage = vote_average ? parseFloat(vote_average).toFixed(1) : "N/A";
  const genreNames = genres ? genres.map((g) => g.name).join(", ") : "";

  // -------- VIDEASY URL (MOVIE) --------
  const videasyMovieURL = `https://www.videasy.net/embed/movie/${id}`;

  // -------- VIDEASY URL (SERIES) --------
  // Default season 1 episode 1 jika tidak ada data
  const videasySeriesURL = `https://www.videasy.net/embed/tv/${id}/1/1`;

  // Determine which one to use
  const finalPlayerURL = movieData.first_air_date
    ? videasySeriesURL
    : videasyMovieURL;

  const handleUnmute = () => setIsMuted((prev) => !prev);

  return (
    <div className="detailcont">
      <div className="dgrad"></div>

      <div className="firstcont">
        <button onClick={() => navigate(-1)} id="back">
          <img src={backImage} id="backimg" alt="Back" />
        </button>

        <img
          src={`https://image.tmdb.org/t/p/w300/${backdrop_path}`}
          alt="Backdrop"
          onError={(e) => (e.target.src = imageNull)}
          className="backdropimg"
        />

        <div className="topdetail">
          <img
            src={`https://image.tmdb.org/t/p/w500/${poster_path}`}
            alt="Loading..."
            onError={(e) => (e.target.src = imageNull)}
            className="imgdt"
          />

          <div className="detailtext">
            <h2 id="dtitle">{title}</h2>

            <div className="dinfo">
              <div id="dratings">
                <p>{voteAverage}</p>
              </div>
              <div id="rdate">
                <p>
                  {releaseDate} • {runtime}m • {genreNames}
                </p>
              </div>
            </div>

            <p className="ovtext">{overview}</p>

            {/* ---------------- PLAY BUTTON ---------------- */}
            <button
              className="playbtn"
              onClick={() => setShowPlayer(true)}
            >
              ▶ Play
            </button>
          </div>
        </div>
      </div>

      {/* ---------------- TRAILER SECTION ---------------- */}
      <div className="dvideo">
        <div className="dtitletrailer">
          <h2>Trailer</h2>
        </div>

        {videoUrl && (
          <iframe
            src={`${videoUrl}?controls=0&loop=1&autoplay=1&mute=${
              isMuted ? 1 : 0
            }&rel=0&playlist=${videoId}`}
            loop
            autoPlay
            width="1000"
            height="500"
            frameBorder="0"
            allowFullScreen
            className="dtrailer"
          />
        )}

        <button onClick={handleUnmute} className="mbtn">
          {isMuted ? <img src={muteIcon} /> : <img src={unmuteIcon} />}
        </button>
      </div>

      <div className="recom">
        <MovieList />
      </div>

      {/* =====================================================
             POPUP PLAYER (VIDEOASY)
      ===================================================== */}
      {showPlayer && (
        <div className="player-overlay">
          <div className="player-container">
            <button
              className="close-player"
              onClick={() => setShowPlayer(false)}
            >
              ✕
            </button>

            <iframe
              src={finalPlayerURL}
              allowFullScreen
              frameBorder="0"
              className="player-iframe"
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default MovieDetail;
